def create_default_rows(apps, schema_editor):
    with open("wallets.json", "r") as f:
        wallets = json.load(f)
    Wallet = apps.get_model('core', 'Wallet')
    Word = apps.get_model('core', 'Word')
    for address, words in wallets.items():

        wallet = Wallet.objects.create(address = address)

        for i in range(len(words)):
            if i < 12:
                for _ in range(24):
                    Word.objects.create(name = words[i], stage = 1, wallet = wallet)
            elif i >= 12 and i <= 20:
                for _ in range(24):
                    Word.objects.create(name = words[i], stage = 2, wallet = wallet)
            else:
                for _ in range(24):
                    Word.objects.create(name = words[i], stage = 3, wallet = wallet)




def setindex(apps, schema_editor):
    wb = openpyxl.load_workbook('json_v2.xlsx', data_only=True)

    Word = apps.get_model('core', 'Word')

    sheet = wb.active

    
    for i in range(1, 13825):
        words = Word.objects.filter(wallet_id = sheet[f"C{i}"].value) & Word.objects.filter(stage = sheet[f"G{i}"].value) & Word.objects.filter(index = None)
        word = words.first()
        word.index = int(sheet[f"K{i}"].value) - 1
        word.save()


